import React, { Component } from "react";
import "../styles/Form.scss";

class Form extends Component {

  constructor() {
    super();
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);

    data.set('name', data.get('name').toUpperCase());
    data.set('company', data.get('company').toUpperCase());

    fetch('/api/form-submit-url', {
      method: 'POST',
      body: data,
    });

    if(!event.target.checkValidity()) {
      this.setState({ displayErrors: true });
      return;
    }
    this.setState({ displayErrors: false })
  }

  render() {

    return (
      <div id="Form">
        <form onSubmit={this.handleSubmit} noValidate class={displayErrors ? 'displayErrors' : ''}>
          <input id="name" name="name" placeholder="Name:" type="text" required />

          <input id="company" name="company" placeholder="Company" type="text" required />

          <button>Send data!</button>
        </form>
      </div>
    );
  }
}



export default Form;
