import React, { Component } from "react";
import PropTypes from 'prop-types';
import "../styles/Form.scss";

class Form extends Component {



  static defaultProps = {
    locked: false,
    focussed: false,
    value: '',
    error: '',
    label: '',
    onChange: () => '',
  };

  constructor(props) {
    super(props);

    this.state = {
      focussed: (props.locked && props.focussed) || false,
      value: props.value || '',
      error: props.value || '',
      label: props.value || '',
    };
  }

  onChange = event => {
    const { id } = this.props;
    const value = event.target.value;
    this.setState({ value, error: '' });
    return this.props.onChange(id, value);
  }


  // constructor(props) {
  //   super(props);
  //   this.state = {}

  //   this.handleChange = this.handleChange.bind(this);
  //   this.handleSubmit = this.handleSubmit.bind(this);
  // }

  // handleChange(event) {
  //   this.setState({value: event.target.value});
  // }

  // handleSubmit(event) {
  //   event.preventDefault();
  // }


  render() {

    const { focussed, value, error, label } = this.state;
    const { id, type, locked } = this.props;

    const fieldClassName = `field ${(locked ? focussed : focussed || value) && 'focussed'} ${locked && !focussed && 'locked'}`;

    return (
      <div id="Form">
        <div class={fieldClassName}>
        {/* <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              placeholder="Name"
              value={this.state.value}
              onChange={this.handleChange}
            />
            <input
              type="text"
              placeholder="Company"
              value={this.state.value}
              onChange={this.handleChange}
            />
            <input
              type="file"
            />
          <input type="submit" value="Submit" />
        </form> */}

        <input 
        id={id} 
        type="text" 
        value={value} 
        placeholder={label} 
        onChange={this.onChange}
        onFocus={() => !locked && this.setState({ focussed: true })}
        onBlur={() => !locked && this.setState({ focussed: false })}
        />
        <label htmlFor={id} className={error && 'error'}>
        {error || label}
      </label>
</div>
      </div>
    );
  }
}

export default Form;
