import React, { Component } from "react";
import { Container, Row, Col } from "reactstrap";
import ReactCardFlip from "react-card-flip";
import Asya from "../assets/pictures/asyam.jpg";
import Kaelen from "../assets/pictures/kaelen.png";
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLinkedin } from "@fortawesome/free-brands-svg-icons";
import "../styles/Contact.scss";

library.add(faLinkedin);
class Contact extends Component {
  constructor() {
    super();
    this.state = {
      isFlipped1: false,
      isFlipped2: false,
      isFlipped3: false,
      isFlipped4: false,
      isFlipped5: false,
      isFlipped6: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState((prevState) => ({ isFlipped1: !prevState.isFlipped1,
                                    isFlipped2:  !prevState.isFlipped2,
                                    isFlipped2:  !prevState.isFlipped2,
                                    isFlipped2:  !prevState.isFlipped2,
                                    isFlipped2:  !prevState.isFlipped2,
                                    isFlipped2:  !prevState.isFlipped2,}));
  }

  render() {
    return (
      <div id="Contact">
        <Container>
          <Row>
            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  {/* <img src={} /> */}
                </div>

                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>About</p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Aditi</h3>
            </Col>

            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  {/* <img src={} /> */}
                </div>
                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>
                    Kaaya is a first year Computer Science major at the
                    University of Texas. Interested in cybersecurity, data, and
                    web technologies.
                  </p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Kaaya</h3>
            </Col>

            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  {/* <img src={} /> */}
                </div>
                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>
                    Terry is a freshman studying Aerospace Engineering at the
                    University if Texas.
                    <br />
                    <a
                      href="https://www.linkedin.com/in/terry-murray-478b0b1ba"
                      target="_blank"
                    >
                      <FontAwesomeIcon icon={["fab", "linkedin"]} />
                    </a>
                  </p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Terry</h3>
            </Col>
          </Row>

          <Row>
            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  <img src={Asya} />
                </div>
                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>Asya is a graduating senior at the University of Texas.</p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Asya</h3>
            </Col>

            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  {/* <img src={} /> */}
                </div>
                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>About</p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Sebastian</h3>
            </Col>

            <Col class="col">
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
              >
                <div class="div" onClick={this.handleClick}>
                  <img src={Kaelen} />
                </div>
                <div class="div-click" onClick={this.handleClick}>
                  {/* <p>Role <br/>  */}
                  <p>
                    Kaelen is a third year Mathematics major at the University
                    of Texas.
                    {/* Interested in data, accessible web technologies,
                    UI/UX design, and environmental sustainability.  */}
                    {/* Website:
                    https://kaelensayth.me/ */}
                    <br />
                    <a
                      href="https://www.linkedin.com/in/kaelensayth/"
                      target="_blank"
                    >
                      <FontAwesomeIcon icon={["fab", "linkedin"]} />
                    </a>
                  </p>
                  {/* link to linkedIn */}
                </div>
              </ReactCardFlip>
              <h3>Kaelen</h3>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Contact;
