import React, { Component } from "react";
import PropTypes from 'prop-types';
import "../styles/Form.scss";

class Form extends Component {

  // constructor(props) {
  //   super(props);
  //   this.state = {}

  //   this.handleChange = this.handleChange.bind(this);
  //   this.handleSubmit = this.handleSubmit.bind(this);
  // }

  // handleChange(event) {
  //   this.setState({value: event.target.value});
  // }

  // handleSubmit(event) {
  //   event.preventDefault();
  // }

  constructor(props) {
    super(props);

    this.state = {
      active: (props.locked && props.active) || false,
      value: props.value || "",
      error: props.value || "",
      label: props.label || "Label",
    };
  }

  changeValue(event) {
    const value = event.target.value;
    this.setState({ value, error: "" });
  }

  handleKeyPress(event) {
    if (event.which == 13) {
      this.setState({ value: this.props.predicted });
    }
  }

  render() {

    const { active, value, error, label } = this.state;
    const { predicted, locked } = this.props;
    const fieldClassName = 'field ${(locked ? active : active || value) && "active"} ${locked && !active && "locked"}';

    return (
      <div id="Form">
        <div class={fieldClassName}>
          {active && value && predicted && predicted.includes(value) && <p class="predicted">{predicted}</p>}
          <input 
          id={1} 
          type="text" 
          value={value} 
          placeholder={label} 
          onChange={this.changeValue.bind(this)}
          onKeyPress={this.handleKeyPress.bind(this)}
          onFocus={() => !locked && this.setState({ active: true })}
          onBlur={() => !locked && this.setState({ active: false })}
          />
          <label htmlFor={1} class={error && "error"}>
            {error || label}
          </label>
        </div>
        {/* <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              placeholder="Name"
              value={this.state.value}
              onChange={this.handleChange}
            />
            <input
              type="text"
              placeholder="Company"
              value={this.state.value}
              onChange={this.handleChange}
            />
            <input
              type="file"
            />
          <input type="submit" value="Submit" />
        </form> */}
      </div>
    );
  }
}



export default Form;
