import axios from 'axios';
import React, { Component } from "react";
import Success from '../components/Success'
import "../styles/Form.scss";

class Form extends Component {

  constructor(props) {
    super(props);
    this.state = {
      selectedFile: null
    };
  }

  // checkExtension = (event) => {
  //   let files = event.target.files;
  //   let err = '';
  //   // const types = ['file/json']; necessary if more than one file type accepted
  //   for (var x = 0; x < files.length; x++) {
  //     if (files[x].type !== 'file/json') {
  //       err += files[x].type + ' is not a supported format\n';
  //     }
  //   };

  //   if (err != '') {
  //     event.target.value = null;
  //     console.log(err);
  //     return false;
  //   }
  //   return true;
  // }


  onFileChange = event => {
    var files = event.target.files;
    if(this.checkExtension(event)) {
      this.setState({ 
        selectedFile: event.target.files[0],
        loaded: 0,
      });
    }

  };

  onFileUpload = () => {
    const formData = new FormData();

    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    axios.post("http://localhost:8000/", formData, {
    })
    .then(function (res) {
      <Success />
    })
    .catch(function (res) {
      var btn = document.querySelector(".submit");
      btn.classList.toggle("fail");
    })
  };

  fileData = () => {
    if (this.state.selectedFile) {

      return(
        <div>
          <p>File Name: {this.state.selectedFile.name}</p>
          <p>File Type: {this.state.selectedFile.type}</p>
        </div>
      )
    }
  }



  render() {

    return (
      <div id="Form">
        <h1>Submit form</h1>
        <form>
          <input type="text" placeholder="Name" />
          <input type="text" placeholder="Company" />
          {/* <input type="file" onChange={this.onFileChange} /> */}
          <div className="form-group files">
            <input type="file" className="form-control" multiple="" onChange={this.onFileChange} />
          </div>
          <button className="submit" onClick={this.onFileUpload}>Submit!</button>
        </form>
        {this.fileData()}
      </div>
    );
  }
}



export default Form;
