import React, { Component } from 'react';
import "../styles/About.scss";

class About extends Component {

  renderInstructions = () => {
    const list = [
      {message: "Fill out the provided json files with the registered devices."},
      {message: "Submit the sheet"},
      {message: "You're all set!"},
    ];
    return list.map((item, index) => {
      <div>
      <p key={index}>
        {item.message}
      </p>
      </div>
    });
  };

  render() {
  return (
    <div id="About">
      <h1>About.</h1>
      <p>Our product notifies hospitals, <i> yes y'all</i>, when their devices' sBOMs - 
      <i>a snazzy term for "Software Bill of Materials" </i>
      -  are not up to date / if there has been a recall on their devices. 
      If there has been a recall, the second part of our product is a software hospitals can purchase that puts up a barrier/firewall 
      between the recalled device and the rest of the network to hide the device from the network and prevent vulnerabilities.</p>
      <p>Sounds simple enough.</p>

      <p>Instructions of Use:</p>
      {this.renderInstructions()}

      <p>Leave the messy stuff to us. 
        You'll be notified via our alert system, email, or notification system if any devices need to be updated.</p>
    </div>
  );
  }
}

export default About;
